import type { DeviceType } from 'deployment-server'
import { useState, useEffect } from 'react'
import { Card, List, Row, Col, Image } from 'antd'
import { BookOutlined, ZoomInOutlined } from '@ant-design/icons'
import { defineFunctionalComponent } from '../util/defineFunctionalComponent'
import { Link } from 'react-router-dom'
import Search from 'antd/lib/input/Search'

const { Meta } = Card

export default defineFunctionalComponent(function Types () {
  const [deviceTypes, setDeviceTypes] = useState<DeviceType[]>([])
  const [visibleDeviceTypes, setVisibleDeviceTypes] = useState<DeviceType[]>([])
  const [search, setSearch] = useState('')

  useEffect(() => {
    const fetchAsync = async (): Promise<void> => {
      const typesRes = await fetch('/api/device-types/')
      const types = await typesRes.json()
      setDeviceTypes(types)
      searchByName(search)
    }

    fetchAsync().catch(console.error)
  }, [])

  useEffect(() => {
    searchByName(search)
  }, [deviceTypes, search])

  const searchByName = (value: string): void => {
    if (value === '' || value == null) {
      setVisibleDeviceTypes(deviceTypes)
      return
    }

    const filteredDeviceType = deviceTypes?.filter((type) =>
      type.name.toLowerCase().includes(value.toLowerCase())
    )
    setVisibleDeviceTypes(filteredDeviceType)
  }

  return (
    <>
      <Row align='middle' style={{ paddingBottom: '10px' }}>
        <Col flex='auto'><h2>Supported Device Types</h2></Col>
        <Col>
          <Search
            placeholder='Search'
            onSearch={(term) => setSearch(term)}
          />
        </Col>
      </Row>
      <Row gutter={[{ xs: 8, sm: 16, md: 24, lg: 32 }, 20]} align='middle'>
        <Col span={24}>
          <List
            style={{
              overflow: 'scroll',
              overflowX: 'hidden',
              padding: '24px',
              maxHeight: '80vh'
            }}
            grid={{ gutter: 32 }}
            dataSource={visibleDeviceTypes}
            renderItem={(deviceData, index) => {
              return (
                <List.Item key={index}>
                  <Card
                    hoverable
                    style={{ width: 360, height: 400 }}
                    cover={
                      <Image
                        height={200}
                        src={deviceData.image}
                        fallback='data:image/png;base64,'
                      />
                    }
                    actions={[
                      <Link to={'/types/' + deviceData.id} key={1}>
                        <ZoomInOutlined /> Inspect
                      </Link>,
                      <a href={deviceData.store} target='_blank' rel='noreferrer' key={2}>
                        <BookOutlined /> Docs
                      </a>
                    ]}
                  >
                    <Meta
                      title={deviceData.name}
                      description={deviceData.fqbn}
                      style={{ padding: '24px' }}
                    />
                  </Card>
                </List.Item>
              )
            }}
          />
        </Col>
      </Row>
    </>
  )
})
